_updateChipView(UpdateChipViewEvent event, Emitter<FilterState> emit, ) { List<PropertytypechipviewItemModel> newList = List<PropertytypechipviewItemModel>.from(state.filterModelObj!.propertytypechipviewItemList); newList[event.index] = newList[event.index].copyWith(isSelected: event.isSelected); emit(state.copyWith(filterModelObj: state.filterModelObj?.copyWith(propertytypechipviewItemList: newList))); } 
_updateChipView1(UpdateChipView1Event event, Emitter<FilterState> emit, ) { List<OptionschipviewItemModel> newList = List<OptionschipviewItemModel>.from(state.filterModelObj!.optionschipviewItemList); newList[event.index] = newList[event.index].copyWith(isSelected: event.isSelected); emit(state.copyWith(filterModelObj: state.filterModelObj?.copyWith(optionschipviewItemList: newList))); } 
